import React, { Component } from "react";
import { MDBBtn, MDBCol, MDBContainer, MDBRow } from "mdbreact";
import Todoitems from './Components/Todoitem/Todoitem';
import Addeditem from './Components/Addeditem/Addeditem';




class App extends Component {

  state ={
    items:[
      {id:1,tododescription:"eating",time:2},
      {id:2,tododescription:"praying",time:4},
      {id:3,tododescription:"study",time:6}
    ]
  }

  deleteditem = (id) =>{
  let itemms =this.state.items;
  let i = itemms.findIndex(ssitem=>ssitem.id == id)
  itemms.splice(i,1)
  this.setState({itemms:itemms})
  }

  addnewitem = (newitem)=>{
   let newaddeditem = this.state.items;
   newaddeditem.push(newitem)

  }

  render() {
    return (
    <div>
      < Todoitems itemsprops={this.state.items} deleteditem ={this.deleteditem} />
      <Addeditem  addnewitem={this.addnewitem}/>
    </div>

    );
  }
}

export default App;
