import React from 'react';
import { MDBBtn, MDBCol, MDBContainer, MDBRow } from "mdbreact";



const Todoitems = (props)=>{
  
    const {itemsprops,deleteditem} = props;
    const lenghthh = itemsprops.length;
    const listitem = lenghthh ? (
        itemsprops.map(item =>{
            return(
                <div key={item.id} >
   <span>{item.tododescription}</span>
   <span onClick={()=> deleteditem(item.id)}>x</span>
                </div>
            )
       })
    ) : (
           <p>there is no todo plans to show</p>
    )
     

    return(
        <div>
               <MDBContainer>
             <MDBRow>
        <MDBCol lg="4" md="12" className="mb-lg-0 mb-4">
          <MDBCard>
            <MDBCardBody>
<p>
          {listitem}
</p>
          </MDBCardBody>
</MDBCard>
</MDBCol>
                </MDBRow>
              </MDBContainer>
            </div>

            

    )
}

export default Todoitems;
