import React, { Component } from "react";
import { MDBRow,MDBContainer, MDBCol,MDBInput, MDBCard, MDBCardBody, MDBIcon, MDBBtn } from "mdbreact";
import style from ''
class Addeditem extends Component {

    state={
       
        tododescription :'' ,
        time : ''

    }
   

    handelchange = (e) => {
       this.setState({
           [e.target.id]:e.target.value 
        

        })
   
       
    }

    handelsubmit = (e) =>{
     e.preventDefault();
       this.props.addnewitem(this.state)
       this.setState({
        tododescription:'',
        time:''
       })
    }

   

    render() {
      return (
        <div>
             <MDBContainer>

                 <MDBRow>
                    <MDBCol>
                         <form>
                         <MDBContainer>

<MDBRow>
   <MDBCol>
              
                    <MDBInput  type="text" placeholder="enter your plane" id="tododescription" 
                    onChange={this.handelchange} value={this.state.tododescription} />
</MDBCol >
   </MDBRow>
   </MDBContainer>



   <MDBContainer>

<MDBRow>
   <MDBCol>
                    <MDBInput type="time" placeholder="enter your time" id="time"
                     onChange={this.handelchange} value={this.state.time} />
                    {/* <button >add</button> */}

                    </MDBCol >
   </MDBRow>
   </MDBContainer>



   
   <MDBContainer>

<MDBRow>
   <MDBCol>
                    <MDBBtn  outline color="white" onClick={this.handelsubmit} className={mybtn} >
              Add
              </MDBBtn>

              </MDBCol >
   </MDBRow>
   </MDBContainer>
                </form>


             


   </MDBCol >
   </MDBRow>
   </MDBContainer>

            </div>
      );
    }
  }
  
  export default Addeditem;
