import React, { Component } from "react";
import { MDBBtn, MDBCol, MDBContainer, MDBRow } from "mdbreact";

class Addeditem extends Component {

    state={
       
        tododescription :'' ,
        time : ''

    }
   

    handelchange = (e) => {
       this.setState({
           [e.target.id]:e.target.value 
        

        })
   
       
    }

    handelsubmit = (e) =>{
     e.preventDefault();
       this.props.addnewitem(this.state)
       this.setState({
           
       })
    }

   

    render() {
      return (
        <div>
                <form>
                    <input type="text" placeholder="enter your plane" id="tododescription" onChange={this.handelchange} />
                    <input type="time" placeholder="enter your time" id="time" onChange={this.handelchange} />
                    <button onClick={this.handelsubmit} >add</button>

                </form>

            </div>
      );
    }
  }
  
  export default Addeditem;
