import React,{ Component , Fragment } from "react";
import { MDBBtn, MDBCol, MDBContainer, MDBRow,MDBInput , MDBBtnGroup, MDBListGroup , MDBInputGroup ,MDBListGroupItem } from "mdbreact";
class Coursrlist extends Component{

     state ={
         isEdit:false
         }

    rendercourse = ()=>{
        return(
            <MDBContainer>

<MDBListGroup style={{ width: "22erm" }}>
    <MDBListGroupItem className="d-flex justify-content-between align-items-center " >{this.props.details.name}
           <MDBBtnGroup size="sm" className="mb-4">
            <MDBBtn  color="red"  onClick={ ()=>{this.props.deletecourse(this.props.index)}}>Delete</MDBBtn>
            <MDBBtn color="warning" onClick={()=>{this.togglestate()}}>Edit</MDBBtn>
          </MDBBtnGroup>
    </MDBListGroupItem>
</MDBListGroup>
          </MDBContainer>
        

        )
    }


    
    togglestate = () =>{
        let {isEdit} = this.state;
        this.setState({
            isEdit:!isEdit
        })
    }

    // updatecourseitem = (e)=>{
    //     e.preventDefault()
    //     this.props.updatcoures(this.props.index ,this.input.value);
    //     this.togglestate();
    //    }

    editcourseitem = (e) =>{
      e.preventDefault();
      this.props.updatcoures(this.props.index,this.input.value)
      this.togglestate();
    }

    renderupdatecourse = () =>{
        return(
          
            <MDBContainer>
   <form>
            <MDBListGroup style={{ width: "22erm" }}>
                <MDBListGroupItem className="d-flex justify-content-between align-items-center " >
{/*             
                <div className="grey-text" > */}
            
               {/* <MDBInput  type="text"  
                  ref={(v)=>{this.input= v}} 
                valueDefault={this.props.details.name}
               
             /> */}
              
                 {/* <input type="text" /> */}
             <input type="text"  className="form-control form-control-lg" defaultValue={this.props.details.name}
                ref={(v)=>{this.input= v}} />


                   <MDBBtnGroup size="sm" className="mb-4">
                   
                   <MDBBtn outline color="success" onClick={this.editcourseitem}>update</MDBBtn>
                 
                 </MDBBtnGroup>
              
             {/* </div> */}
           
                </MDBListGroupItem>
            </MDBListGroup>
            </form>
                      </MDBContainer>
                  
        )
    }

      

    render(){
        let {isEdit}=this.state;
        return(
        
            <Fragment>
                { isEdit ? this.renderupdatecourse() : this.rendercourse()}
          </Fragment>
        )
    }
};
 export default Coursrlist;
