import React,{ Component , Fragment } from "react";
import { MDBBtn, MDBCol, MDBContainer, MDBRow,MDBInput , MDBBtnGroup, MDBListGroup , MDBBadge ,MDBListGroupItem } from "mdbreact";
class Coursrlist extends Component{

     state ={
         isEdit:false
         }

    rendercourse = ()=>{
        return(
            <MDBContainer>

<MDBListGroup style={{ width: "22erm" }}>
    <MDBListGroupItem className="d-flex justify-content-between align-items-center " >{this.props.details.name}
           <MDBBtnGroup size="sm" className="mb-4">
            <MDBBtn  color="red"  onClick={ ()=>{this.props.deletecourse(this.props.index)}}>Delete</MDBBtn>
            <MDBBtn color="warning" onClick={()=>{this.togglestate()}}>Edit</MDBBtn>
          </MDBBtnGroup>
    </MDBListGroupItem>
</MDBListGroup>
          </MDBContainer>
        

        )
    }


    
    togglestate = () =>{
        let {isEdit} = this.state;
        this.setState({
            isEdit:!isEdit
        })
    }

    updatecourseitem = (e)=>{
        e.preventDefault();
        this.props.updatcoures(this.props.index , this.input.value );
        this.togglestate();
       }

    renderupdatecourse = () =>{
        return(
            <MDBContainer>

            <MDBListGroup style={{ width: "22erm" }}>
                <MDBListGroupItem className="d-flex justify-content-between align-items-center " >
                <form >
                <div className="grey-text" >
              
               <MDBInput  group type="text"  
                   ref={(v)=>{this.input= v}} 
                valueDefault={this.props.details.name}
             />
                   <MDBBtnGroup size="sm" className="mb-4">
                   
                   <MDBBtn color="success" onClick={this.updatecourseitem} >update</MDBBtn>
                 
                 </MDBBtnGroup>

             </div>
                     
                      </form>
                </MDBListGroupItem>
            </MDBListGroup>
                      </MDBContainer>
        )
    }

      

    render(){
        let {isEdit}=this.state;
        return(
        
            <Fragment>
                { isEdit ? this.renderupdatecourse() : this.rendercourse()}
          </Fragment>
        )
    }
};
 export default Coursrlist;
