import React, { Component } from "react";
import Todoitems from './Components/Todoitem/Todoitem';
import Addeditem from './Components/Addeditem/Addeditem';
import { MDBRow, MDBCol, MDBCard, MDBCardBody, MDBIcon, MDBBtn, MDBContainer } from "mdbreact";




class App extends Component {

  state ={
    items:[
      {id:1,tododescription:"eating",time:2},
      {id:2,tododescription:"praying",time:4},
      {id:3,tododescription:"study",time:6}
    ]
  }

  deleteditem = (id) =>{
  let itemms =this.state.items;
  let i = itemms.findIndex(ssitem=>ssitem.id == id)
  itemms.splice(i,1)
  this.setState({itemms:itemms})
  }

  addnewitem = (oneitem)=>{
    oneitem.id=Math.random();
   let newaddeditem = this.state.items;
   newaddeditem.push(oneitem);
   this.setState({newaddeditem})

  };

  render() {
    return (

    <div>
         <MDBContainer>
      <MDBRow>
        <MDBCol md="6">
          <MDBCard>
            <MDBCardBody className="mx-4">
              <div className="text-center">
                <h3 className="dark-grey-text mb-5">
                  <strong>list</strong>
                </h3>
              </div>
      <Todoitems itemsprops={this.state.items} deleteditem ={this.deleteditem} />
      <Addeditem addnewitem={this.addnewitem}/>
      </MDBCardBody>
      </MDBCard>
      </MDBCol>
      </MDBRow>
      </MDBContainer>
    </div>


    );
  }
}

export default App;
